import styled from "styled-components";

export const SidebarContainer = styled.aside`
  position: fixed;
  z-index: 999; //999 means it will always be there no matter what
  width: 100%;
  height: 100%;
  background: #0d0d0d;
  display: grid;
  align-items: center;
  top: 0;
  left: 0;
  transition: 0.3s ease-in-out;
  /*if else statement ${({ isOpen }) =>
    isOpen
      ? "100%"
      : "0"} means if sidebar isOpen is true then display 100% if not then hide it*/
  opacity: ${({ isOpen }) => (isOpen ? "100%" : "0")};
  /*drop down effect*/
  top: ${({ isOpen }) => (isOpen ? "0" : "-100%")};
`;
export const CloseIcon = styled.aside`
  position: fixed;
`;

export const Icon = styled.aside`
  position: fixed;
`;

export const SidebarWrapper = styled.aside`
  position: fixed;
`;

export const SidebarMenu = styled.aside`
  position: fixed;
`;

export const SidebarLink = styled.aside`
  position: fixed;
`;
